import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RestaurantCard from './RestaurantCard';
import './RestaurantList.css';

const RestaurantList = () => {
    const [restaurants, setRestaurants] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const [loading, setLoading] = useState(true);
    const restaurantsPerPage = 5;

    useEffect(() => {
        const fetchRestaurants = async (page) => {
            try {
                const response = await axios.get(`http://localhost:3000/api/restaurants?page=${page}&limit=${restaurantsPerPage}`);
                const data = response.data;
                setRestaurants(data.restaurants || []);
                setTotalPages(Math.ceil((data.total || 0) / restaurantsPerPage));
            } catch (error) {
                console.error("Error fetching restaurants:", error);
                setRestaurants([]);
            } finally {
                setLoading(false);
            }
        };

        setLoading(true);
        fetchRestaurants(currentPage);
    }, [currentPage]);

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    return (
        <div>
            <div className="restaurant-list">
                {loading ? (
                    <p>Loading...</p>
                ) : restaurants.length > 0 ? (
                    restaurants.map(restaurant => (
                        <RestaurantCard key={restaurant.id} restaurant={restaurant} />
                    ))
                ) : (
                    <p>No restaurants found</p>
                )}
            </div>
            <Pagination
                currentPage={currentPage}
                totalPages={totalPages}
                onPageChange={handlePageChange}
            />
        </div>
    );
};

const Pagination = ({ currentPage, totalPages, onPageChange }) => {
    const pageNumbers = [];

    for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(i);
    }

    return (
        <nav className="pagination-nav">
            <ul className="pagination">
                {pageNumbers.map(number => (
                    <li key={number} className={`page-item ${currentPage === number ? 'active' : ''}`}>
                        <a onClick={() => onPageChange(number)} href="#!" className="page-link">
                            {number}
                        </a>
                    </li>
                ))}
            </ul>
        </nav>
    );
};

export default RestaurantList;
