import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RestaurantCard from './RestaurantCard';
import './RestaurantList.css';

const RestaurantList = () => {
    const [restaurants, setRestaurants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchRestaurants = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/restaurants'); // Ensure this URL is correct
                setRestaurants(response.data);
            } catch (err) {
                setError(err.message);
                console.error("There was an error fetching the restaurants!", err);
            } finally {
                setLoading(false);
            }
        };

        fetchRestaurants();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="restaurant-list">
            {restaurants.map(restaurant => (
                <RestaurantCard 
                    key={restaurant._id} 
                    restaurant={{
                        ...restaurant,
                        image_url: restaurant.photos_url  // provide a default image if none is available
                    }} 
                />
            ))}
        </div>
    );
};

export default RestaurantList;
